import React, { useState, useEffect } from 'react';
import axios from 'axios';

import ButtonRote from '../ButtonRote/ButtonRote';
import ModalRote from '../ModalRote/ModalRote';

const ClientList = () => {
    const [clients, setClients] = useState([]);
    const [optimizedRoute, setOptimizedRoute] = useState([]);
    const [modalVisible, setModalVisible] = useState(false);


    useEffect(() => {
        const fetchClients = async () => {
            try {
                const response = await axios.get('/api/clientes');
                console.log('Resposta do servidor', r);
                setClients(response.data);
            } catch (error) {
                console.log('Erro ao buscar clientes:', error);
            }
        };
        fetchClients();
    }, []);// Executa so uma vez no carregamento inicial

    const handleOptimizeRoute = async () => {
        try {
            const response = await axios.get('/otimizar-rotas');
            setOptimizedRoute(response.data.ordemVisita);
            setModalVisible(true);
        } catch (error) {
            console.error('Erro ao otimizar rotas:', error);
        }
    };

    return (
        <div>
          <h1>Lista de Clientes</h1>
          <ul>
            {clients && clients.map((client) => (
              <li key={client.id}>{client.nome}</li>
            ))}
          </ul>
          <ButtonRote onClick={handleOptimizeRoute} />
          <ModalRote visible={modalVisible} route={optimizedRoute} onClose={() => setModalVisible(false)} />
        </div>
      );
    };
    
export default ClientList;
